"use client";
import {
  useState,
  useRef,
  TouchEvent,
  MouseEvent,
  useCallback,
  useMemo,
  memo,
} from "react";

interface ImageSliderProps {
  images?: string[];
}

// 인디케이터 컴포넌트 분리 및 메모이제이션
const Indicator = memo(({ index, total }: { index: number; total: number }) => (
  <div>
    {index + 1}/{total}
  </div>
));

Indicator.displayName = "Indicaator";

// 이미지 슬라이드 컴포넌트 분리 및 메모이제이션
const ImageSlide = memo(
  ({ image, className }: { image: string; className: string }) => (
    <div className={className}>
      <div
        className="w-full h-full bg-center bg-cover transform transition-transform will-change-transform"
        style={{ backgroundImage: `url(${image})` }}
      />
    </div>
  ),
);

ImageSlide.displayName = "ImageSlide";

const ImageSlider = ({
  images = [
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
  ],
}: ImageSliderProps) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [touchStart, setTouchStart] = useState(0);
  const [touchEnd, setTouchEnd] = useState(0);
  const [isDragging, setIsDragging] = useState(false);
  const sliderRef = useRef<HTMLDivElement>(null);

  const minSwipeDistance = 50;

  // 이미지 전환 로직 최적화
  const changeSlide = useCallback(
    (direction: "next" | "prev") => {
      setCurrentIndex((prev) => {
        if (direction === "next") {
          return prev === images.length - 1 ? images.length - 1 : prev + 1;
        }
        return prev === 0 ? 0 : prev - 1;
      });
    },
    [images.length],
  );

  // 터치 이벤트 핸들러 최적화
  const handleTouchStart = useCallback((e: TouchEvent) => {
    setTouchStart(e.targetTouches[0].clientX);
    setTouchEnd(e.targetTouches[0].clientX);
    setIsDragging(true);
  }, []);

  const handleTouchMove = useCallback(
    (e: TouchEvent) => {
      if (!isDragging) return;
      setTouchEnd(e.targetTouches[0].clientX);
    },
    [isDragging],
  );

  // 마우스 이벤트 핸들러 최적화
  const handleMouseDown = useCallback((e: MouseEvent) => {
    setIsDragging(true);
    setTouchStart(e.clientX);
    setTouchEnd(e.clientX);
  }, []);

  const handleMouseMove = useCallback(
    (e: MouseEvent) => {
      if (!isDragging) return;
      setTouchEnd(e.clientX);
    },
    [isDragging],
  );

  // 드래그 종료 처리 최적화
  const handleDragEnd = useCallback(() => {
    if (!isDragging) return;

    const swipeDistance = touchEnd - touchStart;
    if (Math.abs(swipeDistance) > minSwipeDistance) {
      changeSlide(swipeDistance > 0 ? "prev" : "next");
    }

    setIsDragging(false);
    setTouchStart(0);
    setTouchEnd(0);
  }, [isDragging, touchEnd, touchStart, changeSlide]);

  // 스타일 메모이제이션
  const sliderStyle = useMemo(
    () => ({
      transform: `translateX(-${currentIndex * 100}%)`,
    }),
    [currentIndex],
  );

  return (
    <div className="relative w-full h-full overflow-hidden">
      <div
        ref={sliderRef}
        className="w-full h-full flex transition-transform object-fill duration-300 ease-out cursor-grab active:cursor-grabbing"
        style={sliderStyle}
        onTouchStart={handleTouchStart}
        onTouchMove={handleTouchMove}
        onTouchEnd={handleDragEnd}
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleDragEnd}
        onMouseLeave={handleDragEnd}
      >
        {images.map((image, index) => (
          <ImageSlide key={index} image={image} className="min-w-full h-full" />
        ))}
      </div>

      <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 flex space-x-2">
        <Indicator index={currentIndex} total={images.length} />
      </div>
    </div>
  );
};

export default memo(ImageSlider);
